function ButtonLink({title, href, onClick}){
    let Component="button";
    const btnProps={};
    if(href){
        btnProps.href=href;
        Component="a";
    }
    if (onClick) {
        btnProps.onClick=onClick;
    }
    return(
        <Component {...btnProps}>{title}</Component>
    )
}
function Input({label,...propsBtn}){
    return(
        <div>
            <label>{label}</label>
            <input {...propsBtn}/>
        </div>
    )
}
function ButtonChildren({children}){
    return(
        <button>{children}</button>
    )
}
function PostItem(props){
    const Form ={
        Input(){
            return <input/>
        },
        Checkbox(){
            return <input type="checkbox"/>
        }
    };
    return(
        <div className="post-item">
            <img src={props.src} alt="minions" width="500"></img>
            <h2> {props.title}</h2>
            <p>{props.des}</p>
            <button 
                onClick={()=>{
                    console.log( Math.random());
                }}            
            >Read more</button>
            <ButtonLink
                title="Author"
                href="https://petrussiqui.github.io"
                onClick={()=>{ console.log( Math.random())}}
            />
            <Input
                label="Input"
                type="text"
                placeholder="Enter your name"
                title="This is input"
            />
            <ButtonChildren>{false || "Don't Click Me!"}</ButtonChildren>
            <br></br><br></br>
        </div>
    );

}
export default PostItem;
