function ButtonLink({title, href, onClick, className}){
    let Component="button";
    const btnProps={};
    if(href){
        btnProps.href=href;
        Component="a";
    }
    if (onClick) {
        btnProps.onClick=onClick;
    }
    return(
        <Component {...btnProps} className={className}>{title}</Component>
    )
}
function Input({label,...propsBtn}){
    return(
        <div>
            <label>{label}</label>
            <input {...propsBtn}/>
        </div>
    )
}
function ButtonChildren({children}){
    return(
        <button>{children}</button>
    )
}
function PostItem(props){
    const Form ={
        Input(){
            return <input/>
        },
        Checkbox(){
            return <input type="checkbox"/>
        }
    };
    // const classes= clsx(style.btnLink, 'd-flex', {
    //     [style.active]: props //boolean truyền từ component
    // })
    return(
        <div className="post-item">
            <img src={props.src} alt="minions" width="100%" height="300px"></img>
            <h2 className={style.heading}> {props.title}</h2>
            <p>{props.des}</p>
            
            <ButtonLink
                title="Author"
                href="https://petrussiqui.github.io"
                onClick={()=>{ console.log( Math.random())}}
                className ={ `${style.btnLink} ${style.active}` }
            /> 
            <button 
                onClick={()=>{
                    console.log( Math.random());
                }}            
            >Read more</button>
        </div>
    );
}

export default PostItem;
